head.script:
var thumbnail_mode = "float-left" ;
summary_noimg = 370;
summary_img = 370;
img_thumb_height = 150;
img_thumb_width = 200;

//
function removeHtmlTag(strx,chop){
if(strx.indexOf("<")!=-1)   
{
var s = strx.split("<");
for(var i=0;i<s.length;i++){
if(s[i].indexOf(">")!=-1){
s[i] = s[i].substring(s[i].indexOf(">")+1,s[i].length);            
}        
}
strx =  s.join("");    
}   
chop = (chop < strx.length-1) ? chop : strx.length-2;
while(strx.charAt(chop-1)!=' ' && strx.indexOf(' ',chop)!=-1) chop++;
strx = strx.substring(0,chop-1);
return strx+' ';
}
function createSummaryAndThumb(pID){
var div = document.getElementById(pID);
var imgtag = "";
var img = div.getElementsByTagName("img");
var summ = summary_noimg;
imgtag = '<span style="float:left; margin: 0px 0px 0px 0px;"><img src="http://2.bp.blogspot.com/_M_YCe5AWy0o/TNVZHuVcl2I/AAAAAAAAAuE/qkp1YzqUheI/s1600/without+image" width="'+img_thumb_width+'px" height="'+img_thumb_height+'px"/></span>';
if(img.length>=1) {
imgtag = '<div id="thumbnail"><div class="thumbnail"><img src="'+img[0].src+'" width="'+img_thumb_width+'px" height="'+img_thumb_height+'px"/></div></div>';
summ = summary_img;
} 
var summary = imgtag + '<p>' + removeHtmlTag(div.innerHTML,summ) + '</p>'; 
div.innerHTML = summary;
}
